public class NineMaxMin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 9. Maximum and minimum number in the array?
		 */
		
		int[] array=new int[8];

        array[0]=50;
        array[1]=10;
        array[2]=25;
        array[3]=15;
        array[4]=100;
        array[5]=35;
        array[6]=65;
        array[7]=45;

        System.out.println("Maximum number is " +array[4]);
        System.out.println("Minimum number is " +array[1]);
	}

}
