public class Exercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Print numbers from 100 to 1
		int t = 100;
		while (t >= 1) {
			System.out.print(t + "  ");
			t--;
		}

		System.out.println("----------------------------------------------");
		System.out.println("----------------------------------------------");

//Print even numbers from 20 to 100 
		
		int a=20;
		while(a<=100) {
			
			System.out.print(a+"  ");
			a+=2;
		}

//Print only odd numbers from 100 to 1
		int b=21;
		while(b<=100) {
			
			System.out.print(b+"  ");
			b+=2;
		}
		
		
		
	}

}
