package class7;

public class num1to100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// 1.Print number from 1 to 100

				for (int i = 1; i <= 100; i++) {
					System.out.print(i+ " ");
				}
				
				
				System.out.println("------------------------------------------");
				System.out.println("------------------------------------------");
			
		//	2.Print number from 100 to 1
				int t = 100;
				while (t >= 1) {
					System.out.print(t + "  ");
					t--;
				}
				
				
	}

}
