package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing numeric values
		byte box1 = 127;
		short box2=100;
		int box3=100000;   //popular used
		long box4=100000000L; //credit cards, tracking number
		
		
		// storing and representing decimal values;
		float variable1=1.99F;
		double variable2=99.95678; //popular used
		
		
		//storing single characters
		char container='a';
		char dollar='$';
		
		
		//storing yes or no values
		boolean hot=true;
		boolean tired=false;
		
		
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		
		System.out.println(variable1);
		System.out.println(variable2);  //printing value of the variable
		
		System.out.println(container);
		System.out.println(dollar);
		
		System.out.println(hot);
		System.out.println(tired);
		
		
		System.out.println("variable2"); //printing text variable
		

		
		
		
		
		
	}

}
