package com.syntax.class02;

public class NonPrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//String is just 1 example of non-primitive data type
		
		String name="Asma";
		
		String address="123 address street";
		
		String phone="123 456 7890";
		
		String age="17"; // anything you put inside "_" -> becomes strings
		
		String character="A";
		
		
		
		// Short cut
		 //syso + Ctlr + Space   => System.out.println();
		
		
		System.out.println(name);
		
		// Want to add 2 string together  "My name is" + Asma
		
		System.out.println("My name is "+name);
		
		
		
		//Asma is 17
		System.out.println(name+" is "+age);
		
		
		
		String fruit="apple";
		double price=1.99;
		
		// The price of the apple is 1.99
		System.out.println("The price of the "+fruit+" is " + price);
		
		fruit="mango";
		price=5.99;
		// The price of the mango is 5.99
		System.out.println("The price of the "+fruit+" is " + price);
		
		
		
		
		
		
		
		
		
	}

}
