package com.syntax.class02;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte cat1;
		cat1 = 17;
		short cat2;
		cat2=150;
		int cat3;
		cat3=122224;
		long cat4; 
		cat4=1097876760L;
		
		
		// storing and representing decimal values;
		float corn1;
		corn1=1.89F;
		double corn2; 
		corn2=99.6789;
		
		
		//storing single characters
		char blue;
		blue='b';
		
		char yellow;
		yellow='y';
		
		
		//storing yes or no values
		boolean winter;
		winter=true;
		
		boolean summer;
		summer=false;
		
		
		System.out.println(cat1);
		System.out.println(cat2);
		System.out.println(cat3);
		System.out.println(cat4);
		
		System.out.println(corn1);
		System.out.println(corn2);
		
		System.out.println(blue);
		System.out.println(yellow);
		
		System.out.println(winter);
		System.out.println(summer);
		
		
		cat3=222222;   // reassign value of cat3
		System.out.println(cat3); // ans= 222222
		
		
		
	}

}
